import React, { useState } from 'react';
import styles from './AddPolicy.module.css'; 
import { useNavigate } from 'react-router-dom';

function AddPolicy() {
  const [policyname, setpolicyname] = useState('');
  const [sessiontimeout, setsessiontimeout] = useState('');
  const [passwordattempts, setpasswordattempts] = useState('');
  const [passwordneverexpires, setpasswordneverexpires] = useState(false);
  const [passwordchangeduration, setpasswordchangeduration] = useState('');
  const [passwordexpirenotification, setpasswordexpirenotification] = useState('');
  const [status, setstatus] = useState('');
  
  // Error states
  const [policyNameError, setPolicyNameError] = useState('');
  const [sessionTimeoutError, setSessionTimeoutError] = useState('');
  const [passwordAttemptsError, setPasswordAttemptsError] = useState('');
  const [passwordChangeDurationError, setPasswordChangeDurationError] = useState('');
  const [passwordExpireNotificationError, setPasswordExpireNotificationError] = useState('');
  const [statusError, setStatusError] = useState('');

  const navigate = useNavigate();

  const handleReset = () => {
    document.getElementById('addPolicyForm').reset(); 
    setpolicyname('');
    setsessiontimeout('');
    setpasswordattempts('');
    setpasswordneverexpires(false);
    setpasswordchangeduration('');
    setpasswordexpirenotification('');
    setstatus('');

    // Reset error states
    setPolicyNameError('');
    setSessionTimeoutError('');
    setPasswordAttemptsError('');
    setPasswordChangeDurationError('');
    setPasswordExpireNotificationError('');
    setStatusError('');
  };

  const validateFields = () => {
    let isValid = true;

    if (!policyname) {
      setPolicyNameError('Policy name is required.');
      isValid = false;
    } else {
      setPolicyNameError('');
    }

    if (!sessiontimeout) {
      setSessionTimeoutError('Session timeout is required.');
      isValid = false;
    } else {
      setSessionTimeoutError('');
    }

    if (!passwordattempts) {
      setPasswordAttemptsError('Password attempts are required.');
      isValid = false;
    } else {
      setPasswordAttemptsError('');
    }

    if (!passwordneverexpires) {
      if (!passwordchangeduration) {
        setPasswordChangeDurationError('Password change duration is required.');
        isValid = false;
      } else if (Number(passwordchangeduration) <= Number(passwordexpirenotification)) {
        setPasswordChangeDurationError('Password change duration must be greater than password expire notification.');
        isValid = false;
      } else {
        setPasswordChangeDurationError('');
      }

      if (!passwordexpirenotification) {
        setPasswordExpireNotificationError('Password expire notification is required.');
        isValid = false;
      } else {
        setPasswordExpireNotificationError('');
      }
    }

    if (!status) {
      setStatusError('Status is required.');
      isValid = false;
    } else {
      setStatusError('');
    }

    return isValid;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateFields()) {
      return;
    }

    // Perform submission logic here

    try {
      const response = await fetch('http://localhost:5000/api/postcompanyitpolicy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          policyname,
          sessiontimeout,
          passwordattempts,
          passwordneverexpires,
          passwordchangeduration: passwordneverexpires ? null : passwordchangeduration,
          passwordexpirenotification: passwordneverexpires ? null : passwordexpirenotification,
          status
        })
      });

      if (response.ok) {
        const result = await response.json();
        console.log('Form submitted successfully:', result);
        handleReset(); 
      } else {
        console.error('Error submitting form.');
      }
    } catch (error) {
      console.error('An error occurred:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className={styles.formContainer} id="addPolicyForm">
        <h1>Company IT Policy Master</h1>
        <div className={styles.formGroup}>
          <label>Policy Name:</label>
          <input
            type="text"
            value={policyname}
            onChange={(e) => setpolicyname(e.target.value)}
          />
          {policyNameError && <p className={styles.error}>{policyNameError}</p>}
        </div>

        <div className={styles.formGroup}>
          <label>Session Time Out (minutes):</label>
          <input
            type="text"
            value={sessiontimeout}
            onChange={(e) => setsessiontimeout(e.target.value)}
          />
          {sessionTimeoutError && <p className={styles.error}>{sessionTimeoutError}</p>}
        </div>

        <div className={styles.formGroup}>
          <label>Password Attempts:</label>
          <input
            type='text'
            value={passwordattempts}
            onChange={(e) => setpasswordattempts(e.target.value)}
          />
          {passwordAttemptsError && <p className={styles.error}>{passwordAttemptsError}</p>}
        </div>

        <div className={styles.formGroup}>
          <label>Password Never Expires:</label>
          <input
            type='checkbox'
            checked={passwordneverexpires}
            onChange={(e) => setpasswordneverexpires(e.target.checked)}
          />
        </div>

        {!passwordneverexpires && (
          <>
          <div className={styles.formGroup}>
            <label>Password Change Duration (Days):</label>
            <input
              type='text'
              value={passwordchangeduration}
              onChange={(e) => setpasswordchangeduration(e.target.value)}
            />
            {passwordChangeDurationError && <p className={styles.error}>{passwordChangeDurationError}</p>}
          </div>

          <div className={styles.formGroup}>
            <label>Password Expire Notification (Days):</label>
            <input
              type='text'
              value={passwordexpirenotification}
              onChange={(e) => setpasswordexpirenotification(e.target.value)}
            />
            {passwordExpireNotificationError && <p className={styles.error}>{passwordExpireNotificationError}</p>}
          </div>
          </>
        )}

        <div className={styles.formGroup}>
          <label>Status:</label>
          <select value={status} onChange={(e) => setstatus(e.target.value)}>
            <option value="">Select</option>
            <option value="1">Active</option>
            <option value="2">Inactive</option>
          </select>
          {statusError && <p className={styles.error}>{statusError}</p>}
        </div>

        <div>
          <button type="submit" className={styles.submitButton}>Submit</button>
          <button type="button" onClick={handleReset} className={styles.resetButton}>Reset</button>
          <button type="button" onClick={() => navigate('/GetPolicy')} className={styles.listButton}>List</button>
        </div>
    </form>
  );
}

export default AddPolicy;
