import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const EditPolicy = () => {
  const { id } = useParams(); 
  const [policy, setPolicy] = useState(null); // Initially null
  const [loading, setLoading] = useState(true); // Track loading state
  const [formData, setFormData] = useState({
    policyname: '',
    passwordattempts: '',
    passwordneverexpires: false,
    passwordchangeduration: '',
    passwordexpirenotification: '',
    status: 1,
  });

  const navigate = useNavigate();

  useEffect(() => {
    const fetchPolicyById = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/getcompanyitpolicy/${id}`);
        if (response.ok) {
          const data = await response.json();
          if (data && data.length > 0) {
            const policyData = data[0]; // Assuming the API returns an array of policies
            setPolicy(policyData);
            setFormData({
              policyname: policyData.policyname,
              passwordattempts: policyData.passwordattempts,
              passwordneverexpires: policyData.passwordneverexpires,
              passwordchangeduration: policyData.passwordchangeduration,
              passwordexpirenotification: policyData.passwordexpirenotification,
              status: policyData.status,
            });
            setLoading(false); // Set loading to false once data is fetched
          } else {
            console.error('No policy found with the given ID');
          }
        } else {
          console.error('Failed to fetch policy');
        }
      } catch (error) {
        console.error('Error fetching policy:', error);
      }
    };
    fetchPolicyById();
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/api/EditPolicy/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert('Policy updated successfully!');
        navigate('/policies');
      } else {
        alert('Failed to update policy');
      }
    } catch (error) {
      console.error('Error updating policy:', error);
      alert('An error occurred while updating the policy.');
    }
  };

  return (
    <div>
      <h1>Edit Policy</h1>
      {loading ? (  // Check for loading state
        <p>Loading policy...</p>
      ) : (
        <form onSubmit={handleSubmit}>
          <div>
            <label>Policy Name:</label>
            <input
              type="text"
              name="policyname"
              value={formData.policyname}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Password Attempts:</label>
            <input
              type="number"
              name="passwordattempts"
              value={formData.passwordattempts}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Password Never Expires:</label>
            <input
              type="checkbox"
              name="passwordneverexpires"
              checked={formData.passwordneverexpires}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Password Change Duration:</label>
            <input
              type="number"
              name="passwordchangeduration"
              value={formData.passwordchangeduration}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Password Expire Notification:</label>
            <input
              type="number"
              name="passwordexpirenotification"
              value={formData.passwordexpirenotification}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Status:</label>
            <select
              name="status"
              value={formData.status}
              onChange={handleInputChange}
            >
              <option value="1">Active</option>
              <option value="2">Inactive</option>
            </select>
          </div>
          <button type="submit">Update Policy</button>
        </form>
      )}
    </div>
  );
};

export default EditPolicy;
