import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './GetPolicy.css'

const GetPolicy = () => {
    const [policies, setPolicies] = useState([]);
    const [filter, setFilter] = useState({
        column: '',
        search: '',
        status: '',
        column2: '',
        search2: '',
    });
    const navigate = useNavigate();
    const [columnOptions, setColumnOptions] = useState([
        'policyname',
        'sessiontimeout',
        'passwordattempts',
        'passwordneverexpires',
        'passwordchangeduration',
        'passwordexpirenotification']);
    const [currentPage, setCurrentPage] = useState(1);
    const policiesPerPage = 10;

    useEffect(() => {
        const fetchPolicies = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/getcompanyitpolicy');
                if (response.ok) {
                    const data = await response.json();
                    setPolicies(data);
                } else {
                    console.error('Failed to fetch policies');
                }
            } catch (error) {
                console.error('Error fetching policies:', error);
            }
        };
        fetchPolicies();
    }, []);

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilter(prevFilter => ({
            ...prevFilter,
            [name]: value
        }));
    };

    const filteredPolicies = policies.filter(policy => {
        const columnMatch = filter.column && filter.search ? policy[filter.column]?.toString().toLowerCase().includes(filter.search.toLowerCase()) : true;
        const column2Match = filter.column2 && filter.search2 ? policy[filter.column2]?.toString().toLowerCase().includes(filter.search2.toLowerCase()) : true;
        const statusMatch = filter.status ? policy.status.toString() === filter.status : true;

        return columnMatch && column2Match && statusMatch;
    });
    const indexOfLastPolicy = currentPage * policiesPerPage;
    const indexOfFirstPolicy = indexOfLastPolicy - policiesPerPage;
    const currentPolicies = filteredPolicies.slice(indexOfFirstPolicy, indexOfLastPolicy);
    const totalPages = Math.ceil(filteredPolicies.length / policiesPerPage);

    const paginate = (pageNumber) => setCurrentPage(pageNumber);
    useEffect(() => {
        setColumnOptions(prevOptions => prevOptions.filter(col => col !== filter.column));
    }, [filter.column]);
  
  const handleEdit = (policyId) => {
    navigate(`/EditPolicy/${policyId}`);
  };

  const handleSuspend = async (id) => {
    try {
        const response = await fetch(`http://localhost:5000/api/suspendPolicy/${id}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            }
        });
        if (response.ok) {
            alert('Policy suspended successfully!');
            const updatedPolicies = policies.map(policy =>
                policy.id === id ? { ...policy, status: 3 } : policy
            );
            setPolicies(updatedPolicies);
        } else {
            console.error('Failed to suspend policy');
            alert('Error suspending the policy. Please try again.');
        }
    } catch (error) {
        console.error('Error during suspension:', error);
        alert('An error occurred. Please check the console for details.');
    }
};

return (
    <div className="container mt-4">
        <h1 className="mb-4">Company IT Policy - Display Screen</h1>
        <div className="filter-row" style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
                <select name="column" value={filter.column} onChange={handleFilterChange}>
                    <option value="">Select Column 1</option>
                    <option value="policyname">Policy Name</option>
                    <option value="sessiontimeout">Session Time Out</option>
                    <option value="passwordattempts">Password Attempts</option>
                    <option value="passwordneverexpires">Password Never Expires</option>
                    <option value="passwordchangeduration">Password Change Duration</option>
                </select>

                <input
                    type="text"
                    name="search"
                    value={filter.search}
                    onChange={handleFilterChange}
                    placeholder="Search in Column 1"
                />

                <select name="column2" value={filter.column2} onChange={handleFilterChange}>
                    <option value="">Select Column 2</option>
                    <option value="policyname" disabled={filter.column === 'policyname'}>Policy Name</option>
                    <option value="sessiontimeout" disabled={filter.column === 'sessiontimeout'}>Session Time Out</option>
                    <option value="passwordattempts" disabled={filter.column === 'passwordattempts'}>Password Attempts</option>
                    <option value="passwordneverexpires" disabled={filter.column === 'passwordneverexpires'}>Password Never Expires</option>
                    <option value="passwordchangeduration" disabled={filter.column === 'passwordchangeduration'}>Password Change Duration</option>
                </select>

                <input
                    type="text"
                    name="search2"
                    value={filter.search2}
                    onChange={handleFilterChange}
                    placeholder="Search in Column 2"
                />

                <select name="status" value={filter.status} onChange={handleFilterChange}>
                    <option value="">Select Status</option>
                    <option value="1">Active</option>
                    <option value="2">InActive</option>
                    <option value="3">Suspended</option>
                </select>
            </div>
        <table className="table table-striped table-bordered"> 
            <thead className="thead-dark">
                <tr>
                    <th scope="col">Serial No.</th>
                    <th scope="col">Policy Name</th>
                    <th scope="col">Session Time Out (minutes)</th>
                    <th scope="col">Password Attempts</th>
                    <th scope="col">Password Never Expires</th>
                    <th scope="col">Password Change Duration</th>
                    <th scope="col">Password Expire Notification</th>
                    <th scope="col">Status</th>
                    <th scope="col">Actions</th>
                </tr>
            </thead>
            <tbody>
            {filteredPolicies.map((policy, index) => (
                        <tr key={policy.id}>
                            <td>{index + 1}</td>
                            <td>{policy.policyname}</td>
                            <td>{policy.sessiontimeout}</td>
                            <td>{policy.passwordattempts}</td>
                            <td>{policy.passwordneverexpires ? 'Yes' : 'No'}</td>
                            <td>{policy.passwordchangeduration}</td>
                            <td>{policy.passwordexpirenotification}</td>
                            <td>{policy.status === 1 ? 'Active' : (policy.status === 3 ? 'Suspended' : 'Inactive')}</td>
                            <td>
                                <div className="d-flex">
                                    <button className="btn btn-primary mr-2" onClick={() => handleEdit(policy.id)}>Edit</button>
                                    <button className="btn btn-warning" onClick={() => handleSuspend(policy.id)}>Suspend</button>
                                </div>
                            </td>
                        </tr>
                    ))}
            </tbody>
        </table>
        <div style={{ marginTop: '20px' }}>
                {Array.from({ length: totalPages }, (_, index) => (
                    <button
                        key={index}
                        onClick={() => paginate(index + 1)}
                        disabled={currentPage === index + 1}
                        style={{
                            marginRight: '5px',
                            padding: '5px 10px',
                            cursor: currentPage === index + 1 ? 'not-allowed' : 'pointer'
                        }}
                    >
                        {index + 1}
                    </button>
                ))}
            </div>
        <button className="btn btn-secondary" onClick={() => navigate(-1)}>Back</button>
    </div>
    );
};
export default GetPolicy;
