import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './GetPolicy.module.css'; // Assuming you have some styles

const GetPolicy = () => {
    const [policies, setPolicies] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchPolicies = async () => {
            try {
            const response = await fetch('http://localhost:5000/api/getcompanyitpolicy');
            if (response.ok) {
                const data = await response.json();
                setPolicies(data);
            } else {
                console.error('Failed to fetch policies');
            }
            } catch (error) {
            console.error('Error fetching policies:', error);
            }
        };
        fetchPolicies();
  }, []);

return (
    <div className={styles.container}>
      <h1>Policy List</h1>
      <table className={styles.policyTable}>
        <thead>
          <tr>
            <th>Policy Name</th>
            <th>Password Attempts</th>
            <th>Password Never Expires</th>
            <th>Password Change Duration</th>
            <th>Password Expire Notification</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {policies.map(policy => (
            <tr key={policy.id}>
              <td>{policy.policyname}</td>
              <td>{policy.passwordattempts}</td>
              <td>{policy.passwordneverexpires ? 'Yes' : 'No'}</td>
              <td>{policy.passwordchangeduration}</td>
              <td>{policy.passwordexpirenotification}</td>
              <td>{policy.status === '1' ? 'Active' : 'Inactive'}</td>
              <td>
                <button onClick={() => handleEdit(policy.id)}>Edit</button>
                <button onClick={() => handleSuspend(policy.id)}>Suspend</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
export default GetPolicy;
