import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const GetPolicy = () => {
    const [policies, setPolicies] = useState([]);
    const [columnFilter1, setColumnFilter1] = useState('');
    const [textFilter1, setTextFilter1] = useState('');
    const [columnFilter2, setColumnFilter2] = useState('');
    const [textFilter2, setTextFilter2] = useState('');
    const [statusFilter, setStatusFilter] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchPolicies = async () => {
            try {
            const response = await fetch('http://localhost:5000/api/getcompanyitpolicy');
            if (response.ok) {
                const data = await response.json();
                setPolicies(data);
            } else {
                console.error('Failed to fetch policies');
            }
            } catch (error) {
            console.error('Error fetching policies:', error);
            }
        };
        fetchPolicies();
  }, []);


  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    if (name === 'columnFilter1') {
      setColumnFilter1(value);
    } else if (name === 'textFilter1') {
      setTextFilter1(value);
    } else if (name === 'columnFilter2') {
      setColumnFilter2(value);
    } else if (name === 'textFilter2') {
      setTextFilter2(value);
    } else if (name === 'statusFilter') {
      setStatusFilter(value);
    }
  };
  
  const filteredPolicies = policies.filter(policy => {
    const matchesFilter1 = columnFilter1
      ? policy[columnFilter1]?.toString().toLowerCase().includes(textFilter1.toLowerCase())
      : true;
  
    const matchesFilter2 = columnFilter2
      ? policy[columnFilter2]?.toString().toLowerCase().includes(textFilter2.toLowerCase())
      : true;
  
    const matchesStatus = statusFilter
      ? policy.status.toString() === statusFilter
      : true;
  
    return matchesFilter1 && matchesFilter2 && matchesStatus;
  });
  
  
  const handleEdit = (policyId) => {
    navigate(`/EditPolicy/${policyId}`);
  };

  const handleSuspend = async (id) => {
    try {
        const response = await fetch(`http://localhost:5000/api/suspendPolicy/${id}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            }
        });
        if (response.ok) {
            alert('Policy suspended successfully!');
            const updatedPolicies = policies.map(policy =>
                policy.id === id ? { ...policy, status: 3 } : policy
            );
            setPolicies(updatedPolicies);
        } else {
            console.error('Failed to suspend policy');
            alert('Error suspending the policy. Please try again.');
        }
    } catch (error) {
        console.error('Error during suspension:', error);
        alert('An error occurred. Please check the console for details.');
    }
};

return (
    <div className="container mt-4">
        <h1 className="mb-4">Company IT Policy - Display Screen</h1>
        <div className="form-row mb-4">
      <div className="form-group col-md-3">
        <label htmlFor="columnFilter1">Filter by Column 1:</label>
        <select
          className="form-control"
          id="columnFilter1"
          name="columnFilter1"
          value={columnFilter1}
          onChange={handleFilterChange}
        >
          <option value="">Select Column</option>
          <option value="policyname">Policy Name</option>
          <option value="sessiontimeout">Session Time Out</option>
          <option value="passwordattempts">Password Attempts</option>
          {/* Add more options as needed */}
        </select>
      </div>
      <div className="form-group col-md-3">
        <label htmlFor="textFilter1">Search in Column 1:</label>
        <input
          type="text"
          className="form-control"
          id="textFilter1"
          name="textFilter1"
          value={textFilter1}
          onChange={handleFilterChange}
        />
      </div>

      <div className="form-group col-md-3">
        <label htmlFor="columnFilter2">Filter by Column 2:</label>
        <select
          className="form-control"
          id="columnFilter2"
          name="columnFilter2"
          value={columnFilter2}
          onChange={handleFilterChange}
        >
          <option value="">Select Column</option>
          <option value="passwordchangeduration">Password Change Duration</option>
          <option value="passwordexpirenotification">Password Expire Notification</option>
          {/* Add more options as needed */}
        </select>
      </div>
      <div className="form-group col-md-3">
        <label htmlFor="textFilter2">Search in Column 2:</label>
        <input
          type="text"
          className="form-control"
          id="textFilter2"
          name="textFilter2"
          value={textFilter2}
          onChange={handleFilterChange}
        />
      </div>
    </div>

    <div className="form-row mb-4">
      <div className="form-group col-md-3">
        <label htmlFor="statusFilter">Filter by Status:</label>
        <select
          className="form-control"
          id="statusFilter"
          name="statusFilter"
          value={statusFilter}
          onChange={handleFilterChange}
        >
          <option value="">Select Status</option>
          <option value="1">Active</option>
          <option value="2">Inactive</option>
          <option value="3">Suspended</option>
        </select>
      </div>
    </div>
        <table className="table table-striped table-bordered"> {/* Added table-bordered class */}
            <thead className="thead-dark">
                <tr>
                    <th scope="col">Serial No.</th>
                    <th scope="col">Policy Name</th>
                    <th scope="col">Session Time Out (minutes)</th>
                    <th scope="col">Password Attempts</th>
                    <th scope="col">Password Never Expires</th>
                    <th scope="col">Password Change Duration</th>
                    <th scope="col">Password Expire Notification</th>
                    <th scope="col">Status</th>
                    <th scope="col">Actions</th>
                </tr>
            </thead>
            <tbody>
                {policies.map((policy, index) => {
                    return (
                        <tr key={policy.id}>
                            <td>{index + 1}</td>
                            <td>{policy.policyname}</td>
                            <td>{policy.sessiontimeout}</td>
                            <td>{policy.passwordattempts}</td>
                            <td>{policy.passwordneverexpires ? 'Yes' : 'No'}</td>
                            <td>{policy.passwordchangeduration}</td>
                            <td>{policy.passwordexpirenotification}</td>
                            <td>{policy.status === 1 ? 'Active' : (policy.status === 3 ? 'Suspended' : 'Inactive')}</td>
                            <td>
                                {policy.status !== 3 && (
                                    <div className="d-flex">
                                        <button
                                            className="btn btn-primary"
                                            onClick={() => handleEdit(policy.id)}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            className="btn btn-warning ml-2"
                                            onClick={() => handleSuspend(policy.id)}
                                        >
                                            Suspend
                                        </button>
                                    </div>
                                )}
                                {policy.status === 3 && <span>-</span>}
                            </td>
                        </tr>
                    );
                })}
            </tbody>
        </table>
        <button className="btn btn-secondary" onClick={() => navigate(-1)}>Back</button>
    </div>
    );
};
export default GetPolicy;
