import React, { useState } from 'react';
import styles from './AddPolicy.module.css'; 

function AddPolicy() {
  const [policyname, setpolicyname] = useState('');
  const [passwordattempts, setpasswordattempts] = useState('');
  const [passwordneverexpires, setpasswordneverexpires] = useState(false);
  const [passwordchangeduration, setpasswordchangeduration] = useState('');
  const [passwordexpirenotification, setpasswordexpirenotification] = useState('');
  const [status, setstatus] = useState('');

  const handleReset = () => {
    document.getElementById('addPolicyForm').reset(); // Reset the form
    setpolicyname('');
    setpasswordattempts('');
    setpasswordneverexpires(false);
    setpasswordchangeduration('');
    setpasswordexpirenotification('');
    setstatus('');
  };


  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted');
  };

  const handleRedirect = () => {
    navigate('/list'); // Redirect to the list page
  };

  return (
    <form onSubmit={handleSubmit} className={styles.formContainer} id="addPolicyForm">
        <h1>Company IT Policy Master</h1>
      <div className={styles.formGroup}>
        <label>Policy Name:</label>
        <input
          type="text"
          value={policyname}
          onChange={(e) => setpolicyname(e.target.value)}
        />
      </div>
      <div className={styles.formGroup}>
        <label>Password Attempts:</label>
        <input
          type='number'
          value={passwordattempts}
          onChange={(e) => setpasswordattempts(e.target.value)}
        />
      </div>
      <div className={styles.formGroup}>
        <label>Password Never Expires:</label>
        <input
          type='checkbox'
          checked={passwordneverexpires}
          onChange={(e) => setpasswordneverexpires(e.target.checked)}
        />
      </div>
      <div className={styles.formGroup}>
        <label>Password Change Duration:</label>
        <input
          type='number'
          value={passwordchangeduration}
          onChange={(e) => setpasswordchangeduration(e.target.value)}
        />
      </div>
      <div className={styles.formGroup}>
        <label>Password Expire Notification:</label>
        <input
          type='number'
          value={passwordexpirenotification}
          onChange={(e) => setpasswordexpirenotification(e.target.value)}
        />
      </div>
      <div className={styles.formGroup}>
        <label>Status:</label>
        <input
          type='text'
          value={status}
          onChange={(e) => setstatus(e.target.value)}
        />
      </div>


      
      <div>
        <button type="submit" className={styles.submitButton}>Submit</button>
        <button type="button" onClick={handleReset} className={styles.ResetButton}>Reset</button>
        <button type="button" onClick={handleRedirect} className={styles.ListButton}>List</button>
      </div>
     
    </form>
  );
}

export default AddPolicy;
