import React, { useState } from 'react';
import styles from './AddPolicy.module.css'; 
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function AddPolicy() {
  const [policyname, setpolicyname] = useState('');
  const [sessiontimeout, setsessiontimeout] = useState('');
  const [passwordattempts, setpasswordattempts] = useState('');
  const [passwordneverexpires, setpasswordneverexpires] = useState(false);
  const [passwordchangeduration, setpasswordchangeduration] = useState('');
  const [passwordexpirenotification, setpasswordexpirenotification] = useState('');
  const [status, setstatus] = useState('');
  const navigate = useNavigate();

  const handleReset = () => {
    document.getElementById('addPolicyForm').reset(); 
    setpolicyname('');
    setsessiontimeout('');
    setpasswordattempts('');
    setpasswordneverexpires(false);
    setpasswordchangeduration('');
    setpasswordexpirenotification('');
    setstatus('');
  };

  const validatePolicyName = (name) => {
    if (!name) {
      return 'Policy name cannot be empty.';
    }

    const startsWithAlphanumericRegex = /^[a-zA-Z0-9]/;
    const endsWithAlphanumericRegex = /[a-zA-Z0-9]$/;
    const noConsecutiveUnderscoresRegex = /__+/;
    const alphanumericUnderscoreRegex = /^[a-zA-Z0-9_]*$/;

    if (!startsWithAlphanumericRegex.test(name) || !endsWithAlphanumericRegex.test(name)) {
      return 'Policy name must start and end with an alphanumeric character.';
    }

    if (!alphanumericUnderscoreRegex.test(name)) {
      return 'Policy name can only contain alphanumeric characters and underscores.';
    }

    if (noConsecutiveUnderscoresRegex.test(name)) {
      return 'Policy name cannot contain consecutive underscores.';
    }

    if (/([a-zA-Z0-9])\1\1/.test(name)) {
      return 'Policy name cannot contain three consecutive identical characters.';
    }

    return '';
  };

  const handlePolicyNameChange = (e) => {
    const name = e.target.value;
    setpolicyname(name);

    const validationError = validatePolicyName(name);
    if (validationError) {
      toast.error(validationError);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = {
      policyname,
      sessiontimeout,
      passwordattempts,
      passwordneverexpires,
      passwordchangeduration: passwordneverexpires ? null : passwordchangeduration,
      passwordexpirenotification: passwordneverexpires ? null : passwordexpirenotification,
      status
    };

    const validationError = validatePolicyName(policyname);
    if (validationError) {
      toast.error(validationError);  
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/postcompanyitpolicy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        const result = await response.json();
        console.log('Form submitted successfully:', result);
        handleReset(); 
        toast.success('Form submitted and data stored successfully!');
      } else {
        toast.error('Error submitting form. Please try again.'); 
      }
    } catch (error) {
      toast.error('An error occurred. Please check the console for details.');
    }
  };

  const handleRedirect = () => {
    navigate('/GetPolicy'); 
  };

  return (
    <form onSubmit={handleSubmit} className={styles.formContainer} id="addPolicyForm">
        <h1>Company IT Policy Master</h1>
      <div className={styles.formGroup}>
        <label>Policy Name:</label>
        <input
          type="text"
          value={policyname}
          onChange={handlePolicyNameChange}
        />
      </div>
      <div className={styles.formGroup}>
        <label>Session Time Out (minutes):</label>
        <input
          type="text"
          value={sessiontimeout}
          onChange={(e) => setsessiontimeout(e.target.value)}
        />
      </div>
      <div className={styles.formGroup}>
        <label>Password Attempts:</label>
        <input
          type='text'
          value={passwordattempts}
          onChange={(e) => setpasswordattempts(e.target.value)}
        />
      </div>
      <div className={styles.formGroup}>
        <label>Password Never Expires:</label>
        <input
          type='checkbox'
          checked={passwordneverexpires}
          onChange={(e) => setpasswordneverexpires(e.target.checked)}
        />
      </div>
      {!passwordneverexpires && (
        <>
      <div className={styles.formGroup}>
        <label>Password Change Duration:</label>
        <input
          type='text'
          value={passwordchangeduration}
          onChange={(e) => setpasswordchangeduration(e.target.value)}
        />
      </div>
      <div className={styles.formGroup}>
        <label>Password Expire Notification:</label>
        <input
          type='text'
          value={passwordexpirenotification}
          onChange={(e) => setpasswordexpirenotification(e.target.value)}
        />
      </div>
      </>
        )}
      <div className={styles.formGroup}>
        <label>Status:</label>
        <select value={status} 
                onChange={(e) => setstatus(e.target.value)}>
            <option value="">Select</option>
            <option value="1">Active</option>
            <option value="2">Inactive</option>
        </select>
      </div>

      <div>
        <button type="submit" className={styles.submitButton}>Submit</button>
        <button type="button" onClick={handleReset} className={styles.ResetButton}>Reset</button>
        <button type="button" onClick={handleRedirect} className={styles.ListButton}>List</button>
      </div>
      <ToastContainer />
    </form>
  );
}

export default AddPolicy;
