import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styles from './AddPolicy.module.css'; // Import the CSS module
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'; // Import toastify styles

const EditPolicy = () => {
  const { id } = useParams();
  const [policy, setPolicy] = useState(null);
  const [loading, setLoading] = useState(true);
  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});
  const [formData, setFormData] = useState({
    policyname: '',
    sessiontimeout: '',
    passwordattempts: '',
    passwordneverexpires: false,
    passwordchangeduration: '',
    passwordexpirenotification: '',
    status: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPolicyById = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/getcompanyitpolicy/${id}`);
        if (response.ok) {
          const data = await response.json();
          console.log("data", data);

          if (data) {
            setPolicy(data);
            setFormData({
              policyname: data.policyname,
              sessiontimeout: data.sessiontimeout,
              passwordattempts: data.passwordattempts,
              passwordneverexpires: data.passwordneverexpires,
              passwordchangeduration: data.passwordchangeduration,
              passwordexpirenotification: data.passwordexpirenotification,
              status: data.status,
            });
            setLoading(false); // Set loading to false once the data is ready
          } else {
            setErrors(prev => ({ ...prev, fetch: 'No policy found with the given ID' }));
          }
        } else {
          setErrors(prev => ({ ...prev, fetch: 'Failed to fetch policy' }));
        }
      } catch (error) {
        console.error('Error fetching policy:', error);
        setErrors(prev => ({ ...prev, fetch: 'Error fetching policy' }));
      }
    };

    fetchPolicyById();
  }, [id]);

  const validateSessionTimeout = (value) => {
    if (value === '' || isNaN(value) || Number(value) > 999) {
      return 'Session timeout must be a number less than or equal to 999.';
    }
    return '';
  };

  const validatePasswordChangeDuration = (value) => {
    if (value === '' || isNaN(value) || Number(value) > 999) {
      return 'Password Change Duration must be a number less than or equal to 999.';
    }
    return '';
  };

  const validatePasswordExpireNotification = (value) => {
    if (value === '' || isNaN(value) || Number(value) > 999) {
      return 'Password Expire Notification must be a number less than or equal to 999.';
    }
    return '';
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const newValue = type === 'checkbox' ? checked : value;

    setFormData(prevFormData => {
      const newData = {
        ...prevFormData,
        [name]: newValue,
      };

      if (name === 'passwordneverexpires') {
        if (checked) {
          newData.passwordchangeduration = '';
          newData.passwordexpirenotification = '';
        }
      }

      return newData;
    });

    // Mark the field as touched
    setTouched(prevTouched => ({
      ...prevTouched,
      [name]: true,
    }));
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.policyname) {
      errors.policyname = 'Policy Name is required';
    }

    const sessionTimeoutError = validateSessionTimeout(formData.sessiontimeout);
    if (sessionTimeoutError) {
      errors.sessiontimeout = sessionTimeoutError;
    }

    if (formData.passwordattempts === '' || isNaN(formData.passwordattempts) || Number(formData.passwordattempts) > 999) {
      errors.passwordattempts = 'Password Attempts must be a number less than or equal to 999';
    }

    if (!formData.passwordneverexpires) {
      const passwordChangeDurationError = validatePasswordChangeDuration(formData.passwordchangeduration);
      if (passwordChangeDurationError) {
        errors.passwordchangeduration = passwordChangeDurationError;
      }

      const passwordExpireNotificationError = validatePasswordExpireNotification(formData.passwordexpirenotification);
      if (passwordExpireNotificationError) {
        errors.passwordexpirenotification = passwordExpireNotificationError;
      }

      if (Number(formData.passwordchangeduration) <= Number(formData.passwordexpirenotification)) {
        errors.passwordchangeduration = 'Password Change Duration must be greater than Password Expire Notification';
      }
    }

    setErrors(errors);

    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Mark all fields as touched before validation
    setTouched({
      policyname: true,
      sessiontimeout: true,
      passwordattempts: true,
      passwordchangeduration: true,
      passwordexpirenotification: true,
      status: true,
    });

    if (!validateForm()) {
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/EditPolicy/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...formData,
          passwordchangeduration: formData.passwordneverexpires ? null : formData.passwordchangeduration,
          passwordexpirenotification: formData.passwordneverexpires ? null : formData.passwordexpirenotification,
        }),
      });

      if (response.ok) {
        toast.success('Policy updated successfully!');
        navigate('/GetPolicy'); // Redirect to list page
      } else {
        toast.error('Failed to update policy');
      }
    } catch (error) {
      console.error('Error updating policy:', error);
      toast.error('An error occurred while updating the policy.');
    }
  };

  const handleRedirect = () => {
    navigate('/GetPolicy');
  };

  const handleReset = () => {
    setFormData({
      policyname: policy.policyname || '',
      sessiontimeout: '',
      passwordattempts: '',
      passwordneverexpires: false,
      passwordchangeduration: '',
      passwordexpirenotification: '',
      status: policy.status || '',
    });
    setErrors({});
    setTouched({});
  };

  return (
    <div className={styles.formContainer}>
      <ToastContainer /> {/* Add this to show toast messages */}
      <h1>Company IT Policy Master</h1>
      {errors.fetch && <div className={styles.error}>{errors.fetch}</div>}
      {loading ? (
        <p>Loading policy...</p>
      ) : (
        <form onSubmit={handleSubmit}>
          <div className={styles.formGroup}>
            <label>Policy Name:</label>
            <input
              type="text"
              name="policyname"
              value={formData.policyname}
              onChange={handleInputChange}
              readOnly 
            />
            {touched.policyname && errors.policyname && <span className={styles.error}>{errors.policyname}</span>}
          </div>
          <div className={styles.formGroup}>
            <label>Session Timeout:</label>
            <input
              type="number"
              name="sessiontimeout"
              value={formData.sessiontimeout}
              onChange={handleInputChange}
            />
            {touched.sessiontimeout && errors.sessiontimeout && <span className={styles.error}>{errors.sessiontimeout}</span>}
          </div>
          <div className={styles.formGroup}>
            <label>Password Attempts:</label>
            <input
              type="text"
              name="passwordattempts"
              value={formData.passwordattempts}
              onChange={handleInputChange}
            />
            {touched.passwordattempts && errors.passwordattempts && <span className={styles.error}>{errors.passwordattempts}</span>}
          </div>
          <div className={styles.formGroup}>
            <label>Password Never Expires:</label>
            <input
              type="checkbox"
              name="passwordneverexpires"
              checked={formData.passwordneverexpires}
              onChange={handleInputChange}
            />
          </div>
          {!formData.passwordneverexpires && (
            <>
              <div className={styles.formGroup}>
                <label>Password Change Duration:</label>
                <input
                  type="number"
                  name="passwordchangeduration"
                  value={formData.passwordchangeduration}
                  onChange={handleInputChange}
                />
                {touched.passwordchangeduration && errors.passwordchangeduration && <span className={styles.error}>{errors.passwordchangeduration}</span>}
              </div>
              <div className={styles.formGroup}>
                <label>Password Expire Notification:</label>
                <input
                  type="number"
                  name="passwordexpirenotification"
                  value={formData.passwordexpirenotification}
                  onChange={handleInputChange}
                />
                {touched.passwordexpirenotification && errors.passwordexpirenotification && <span className={styles.error}>{errors.passwordexpirenotification}</span>}
              </div>
            </>
          )}
          <div className={styles.formGroup}>
            <label>Status:</label>
            <select
              name="status"
              value={formData.status}
              onChange={handleInputChange}
            >
              <option value="">Select</option>
              <option value="1">Active</option>
              <option value="2">Inactive</option>
            </select>
          </div>
          <button type="submit" className={styles.submitButton}>Submit</button>
          <button type="button" onClick={handleReset} className={styles.resetButton}>Reset</button>
          <button type="button" onClick={handleRedirect} className={styles.listButton}>List</button>
        </form>
      )}
    </div>
  );
};

export default EditPolicy;
