import React, { useState } from 'react';
import styles from './AddPolicy.module.css'; 
import { useNavigate } from 'react-router-dom';

function AddPolicy() {
  const [policyname, setpolicyname] = useState('');
  const [sessiontimeout, setsessiontimeout] = useState('');
  const [passwordattempts, setpasswordattempts] = useState('');
  const [passwordneverexpires, setpasswordneverexpires] = useState(false);
  const [passwordchangeduration, setpasswordchangeduration] = useState('');
  const [passwordexpirenotification, setpasswordexpirenotification] = useState('');
  const [status, setstatus] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleReset = () => {
    document.getElementById('addPolicyForm').reset(); 
    setpolicyname('');
    setsessiontimeout('');
    setpasswordattempts('');
    setpasswordneverexpires(false);
    setpasswordchangeduration('');
    setpasswordexpirenotification('');
    setstatus('');
    setErrors({});
  };

  const validatePolicyName = (name) => {
    const errors = {};

    if (!name) {
      errors.policyname = 'Policy name cannot be empty.';
    } else {
      const startsWithAlphanumericRegex = /^[a-zA-Z0-9]/;
      const endsWithAlphanumericRegex = /[a-zA-Z0-9]$/;
      const noConsecutiveUnderscoresRegex = /__+/;
      const alphanumericUnderscoreRegex = /^[a-zA-Z0-9_]*$/;

      if (!startsWithAlphanumericRegex.test(name) || !endsWithAlphanumericRegex.test(name)) {
        errors.policyname = 'Policy name must start and end with an alphanumeric character.';
      } else if (!alphanumericUnderscoreRegex.test(name)) {
        errors.policyname = 'Policy name can only contain alphanumeric characters and underscores.';
      } else if (noConsecutiveUnderscoresRegex.test(name)) {
        errors.policyname = 'Policy name cannot contain consecutive underscores.';
      } else if (/([a-zA-Z0-9])\1\1/.test(name)) {
        errors.policyname = 'Policy name cannot contain three consecutive identical characters.';
      }
    }

    //return errors;
  };

  const validateSessionTimeout = (timeout) => {
    const errors = {};

    if (!timeout) {
      errors.sessiontimeout = 'Session Timeout cannot be empty.';
    } else if (isNaN(timeout) || timeout < 1 || timeout > 999) {
      errors.sessiontimeout = 'Session Timeout must be a number between 1 and 999.';
    }

    //return errors;
  };

  const validatePasswordAttempts = (attempts) => {
    const errors = {};

    if (!attempts) {
      errors.passwordattempts = 'Password Attempts cannot be empty.';
    } else if (isNaN(attempts) || attempts < 1 || attempts > 10) {
      errors.passwordattempts = 'Password Attempts must be a number between 1 and 10.';
    }

   //return errors;
  };

  const validatePasswordChangeDuration = (duration) => {
    const errors = {};

    if (!duration) {
      errors.passwordchangeduration = 'Password Change Duration cannot be empty.';
    } else if (isNaN(duration) || duration < 1 || duration > 999) {
      errors.passwordchangeduration = 'Password Change Duration must be a number between 1 and 999.';
    }

    //return errors;
  };

  const validatePasswordExpireNotification = (notification) => {
    const errors = {};

    if (!notification) {
      errors.passwordexpirenotification = 'Password Expire Notification cannot be empty.';
    } else if (isNaN(notification) || notification < 1 || notification > 999) {
      errors.passwordexpirenotification = 'Password Expire Notification must be a number between 1 and 999.';
    }

    //return errors;
  };

  const handlePolicyNameChange = (e) => {
    const name = e.target.value;
    setpolicyname(name);
    const validationErrors = validatePolicyName(name);
    setErrors(prevErrors => ({ ...prevErrors, ...validationErrors }));
  };

  const handleSessionTimeoutChange = (e) => {
    const timeout = e.target.value;
    setsessiontimeout(timeout);
    const validationErrors = validateSessionTimeout(timeout);
    setErrors(prevErrors => ({ ...prevErrors, ...validationErrors }));
  };

  const handlePasswordAttemptsChange = (e) => {
    const attempts = e.target.value;
    setpasswordattempts(attempts);
    const validationErrors = validatePasswordAttempts(attempts);
    setErrors(prevErrors => ({ ...prevErrors, ...validationErrors }));
  };

  const handlePasswordChangeDurationChange = (e) => {
    const duration = e.target.value;
    setpasswordchangeduration(duration);
    const validationErrors = validatePasswordChangeDuration(duration);
    setErrors(prevErrors => ({ ...prevErrors, ...validationErrors }));
  };

  const handlePasswordExpireNotificationChange = (e) => {
    const notification = e.target.value;
    setpasswordexpirenotification(notification);
    const validationErrors = validatePasswordExpireNotification(notification);
    setErrors(prevErrors => ({ ...prevErrors, ...validationErrors }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate all fields
    const policyNameErrors = validatePolicyName(policyname);
    const sessionTimeoutErrors = validateSessionTimeout(sessiontimeout);
    const passwordAttemptsErrors = validatePasswordAttempts(passwordattempts);
    const passwordChangeDurationErrors = passwordneverexpires ? {} : validatePasswordChangeDuration(passwordchangeduration);
    const passwordExpireNotificationErrors = passwordneverexpires ? {} : validatePasswordExpireNotification(passwordexpirenotification);
    const validationErrors = { ...policyNameErrors, ...sessionTimeoutErrors, ...passwordAttemptsErrors, ...passwordChangeDurationErrors, ...passwordExpireNotificationErrors };

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    // Prepare and submit the form data
    const formData = {
      policyname,
      sessiontimeout,
      passwordattempts,
      passwordneverexpires,
      passwordchangeduration: passwordneverexpires ? null : passwordchangeduration,
      passwordexpirenotification: passwordneverexpires ? null : passwordexpirenotification,
      status
    };

    try {
      const response = await fetch('http://localhost:5000/api/postcompanyitpolicy', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        const result = await response.json();
        console.log('Form submitted successfully:', result);
        handleReset(); 
        alert('Form submitted and data stored successfully!');
      } else {
        alert('Error submitting form. Please try again.'); 
      }
    } catch (error) {
      alert('An error occurred. Please check the console for details.');
    }
  };

  const handleRedirect = () => {
    navigate('/GetPolicy'); 
  };

  return (
    <form onSubmit={handleSubmit} className={styles.formContainer} id="addPolicyForm">
      <h1>Company IT Policy Master</h1>
      <div className={styles.formGroup}>
        <label>Policy Name:</label>
        <input
          type="text"
          value={policyname}
          onChange={handlePolicyNameChange}
        />
        {errors.policyname && <p className={styles.error}>{errors.policyname}</p>}
      </div>
      <div className={styles.formGroup}>
        <label>Session Time Out (minutes):</label>
        <input
          type="text"
          value={sessiontimeout}
          onChange={handleSessionTimeoutChange}
        />
        {errors.sessiontimeout && <p className={styles.error}>{errors.sessiontimeout}</p>}
      </div>
      <div className={styles.formGroup}>
        <label>Password Attempts:</label>
        <input
          type='text'
          value={passwordattempts}
          onChange={handlePasswordAttemptsChange}
        />
        {errors.passwordattempts && <p className={styles.error}>{errors.passwordattempts}</p>}
      </div>
      <div className={styles.formGroup}>
        <label>Password Never Expires:</label>
        <input
          type='checkbox'
          checked={passwordneverexpires}
          onChange={(e) => {
            setpasswordneverexpires(e.target.checked);
            if (e.target.checked) {
              // Hide fields and clear errors if checkbox is checked
              setpasswordchangeduration('');
              setpasswordexpirenotification('');
              setErrors(prevErrors => {
                const newErrors = { ...prevErrors };
                delete newErrors.passwordchangeduration;
                delete newErrors.passwordexpirenotification;
                return newErrors;
              });
            }
          }}
        />
      </div>
      {!passwordneverexpires && (
        <>
          <div className={styles.formGroup}>
            <label>Password Change Duration:</label>
            <input
              type='text'
              value={passwordchangeduration}
              onChange={handlePasswordChangeDurationChange}
            />
            {errors.passwordchangeduration && <p className={styles.error}>{errors.passwordchangeduration}</p>}
          </div>
          <div className={styles.formGroup}>
            <label>Password Expire Notification:</label>
            <input
              type='text'
              value={passwordexpirenotification}
              onChange={handlePasswordExpireNotificationChange}
            />
            {errors.passwordexpirenotification && <p className={styles.error}>{errors.passwordexpirenotification}</p>}
          </div>
        </>
      )}
      <div className={styles.formGroup}>
        <label>Status:</label>
        <select value={status} onChange={(e) => setstatus(e.target.value)}>
          <option value="">Select</option>
          <option value="1">Active</option>
          <option value="2">Inactive</option>
        </select>
      </div>
      <div>
        <button type="submit" className={styles.submitButton}>Submit</button>
        <button type="button" onClick={handleReset} className={styles.ResetButton}>Reset</button>
        <button type="button" onClick={handleRedirect} className={styles.ListButton}>List</button>
      </div>
    </form>
  );
}

export default AddPolicy;
