import React, { useState } from 'react';
import styles from './AddPolicy.module.css'; 
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function AddPolicy() {
  const [policyname, setPolicyname] = useState('');
  const [sessiontimeout, setSessiontimeout] = useState('');
  const [passwordattempts, setPasswordAttempts] = useState('');
  const [passwordneverexpires, setPasswordNeverExpires] = useState(false);
  const [passwordchangeduration, setPasswordChangeDuration] = useState('');
  const [passwordexpirenotification, setPasswordExpireNotification] = useState('');
  const [status, setStatus] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleReset = () => {
    document.getElementById('addPolicyForm').reset(); 
    setPolicyname('');
    setSessiontimeout('');
    setPasswordAttempts('');
    setPasswordNeverExpires(false);
    setPasswordChangeDuration('');
    setPasswordExpireNotification('');
    setStatus('');
    setErrors({});
  };

  const validatePolicyName = (name) => {
    const alphanumericUnderscoreRegex = /^[a-zA-Z0-9_]*$/;
    const startsWithAlphanumericRegex = /^[a-zA-Z0-9]/;
    const noConsecutiveCharsRegex = /(?!.*([a-zA-Z0-9_])\1\1)/;

    if (!startsWithAlphanumericRegex.test(name)) {
      return 'Policy name must start with an alphanumeric character.';
    }
    if (!alphanumericUnderscoreRegex.test(name)) {
      return 'Policy name can only contain alphanumeric characters and underscores.';
    }
    if (!noConsecutiveCharsRegex.test(name)) {
      return 'Policy name cannot contain three consecutive identical characters.';
    }
    return '';
  };

  const validateSessionTimeout = (value) => {
    if (value === '' || isNaN(value) || Number(value) > 999) {
      return 'Session timeout must be a number less than or equal to 999.';
    }
    return '';
  };

  const validatePasswordAttempts = (value) => {
    if (value === '' || isNaN(value) || Number(value) > 999) {
      return 'Password attempts must be a number less than or equal to 999.';
    }
    return '';
  };

  const validatePasswordChangeDuration = (value) => {
    if (value === '' || isNaN(value) || Number(value) > 999) {
      return 'Password change duration must be a number less than or equal to 999.';
    }
    return '';
  };

  const validatePasswordExpireNotification = (value) => {
    if (value === '' || isNaN(value) || Number(value) > 999) {
      return 'Password expire notification must be a number less than or equal to 999.';
    }
    return '';
  };

  const checkDurationVsNotification = () => {
    if (!passwordneverexpires) {
      const duration = Number(passwordchangeduration);
      const notification = Number(passwordexpirenotification);
      if (duration <= notification) {
        setErrors(prev => ({ ...prev, passwordchangeduration: 'Password change duration must be greater than password expiration notification.' }));
        return false;
      }
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newErrors = {};
    
    const policyNameError = validatePolicyName(policyname);
    if (policyNameError) {
      newErrors.policyname = policyNameError;
    }

    const sessionTimeoutError = validateSessionTimeout(sessiontimeout);
    if (sessionTimeoutError) {
      newErrors.sessiontimeout = sessionTimeoutError;
    }

    const passwordAttemptsError = validatePasswordAttempts(passwordattempts);
    if (passwordAttemptsError) {
      newErrors.passwordattempts = passwordAttemptsError;
    }

    if (!status) {
      newErrors.status = '*';
    }

    if (!passwordneverexpires) {
      const passwordChangeDurationError = validatePasswordChangeDuration(passwordchangeduration);
      if (passwordChangeDurationError) {
        newErrors.passwordchangeduration = passwordChangeDurationError;
      }

      const passwordExpireNotificationError = validatePasswordExpireNotification(passwordexpirenotification);
      if (passwordExpireNotificationError) {
        newErrors.passwordexpirenotification = passwordExpireNotificationError;
      }

      if (!checkDurationVsNotification()) {
        newErrors.passwordchangeduration = 'Password change duration must be greater than password expiration notification.';
      }
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      try {
        const response = await fetch('http://localhost:5000/api/postcompanyitpolicy', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            policyname,
            sessiontimeout,
            passwordattempts,
            passwordneverexpires,
            passwordchangeduration: passwordneverexpires ? null : passwordchangeduration,
            passwordexpirenotification: passwordneverexpires ? null : passwordexpirenotification,
            status
          })
        });

        if (response.ok) {
          const result = await response.json();
          console.log('Form submitted successfully:', result);
          handleReset();
          toast.success('Form submitted and data stored successfully!');
        } else {
          toast.error('Error submitting form. Please try again.');
        }
      } catch (error) {
        toast.error('An error occurred. Please check the console for details.');
      }
    }
  };

  return (
    <form onSubmit={handleSubmit} className={styles.formContainer} id="addPolicyForm">
      <h1>Company IT Policy Master</h1>
      <ToastContainer />
      
      <div className={styles.formGroup}>
        <label>Policy Name:</label>
        <input
          type="text"
          value={policyname}
          onChange={(e) => {
            setPolicyname(e.target.value);
            const error = validatePolicyName(e.target.value);
            setErrors(prev => ({ ...prev, policyname: error }));
          }}
        />
        {errors.policyname && <span className={styles.error}>{errors.policyname}</span>}
      </div>

      <div className={styles.formGroup}>
        <label>Session Timeout:</label>
        <input
          type="text"
          value={sessiontimeout}
          onChange={(e) => {
            setSessiontimeout(e.target.value);
            const error = validateSessionTimeout(e.target.value);
            setErrors(prev => ({ ...prev, sessiontimeout: error }));
          }}
        />
        {errors.sessiontimeout && <span className={styles.error}>{errors.sessiontimeout}</span>}
      </div>

      <div className={styles.formGroup}>
        <label>Password Attempts:</label>
        <input
          type="text"
          value={passwordattempts}
          onChange={(e) => {
            setPasswordAttempts(e.target.value);
            const error = validatePasswordAttempts(e.target.value);
            setErrors(prev => ({ ...prev, passwordattempts: error }));
          }}
        />
        {errors.passwordattempts && <span className={styles.error}>{errors.passwordattempts}</span>}
      </div>

      <div className={styles.formGroup}>
        <label>Password Never Expires:</label>
        <input
          type="checkbox"
          checked={passwordneverexpires}
          onChange={(e) => setPasswordNeverExpires(e.target.checked)}
        />
      </div>

      {!passwordneverexpires && (
        <>
          <div className={styles.formGroup}>
            <label>Password Change Duration:</label>
            <input
              type="text"
              value={passwordchangeduration}
              onChange={(e) => {
                setPasswordChangeDuration(e.target.value);
                const error = validatePasswordChangeDuration(e.target.value);
                setErrors(prev => ({ ...prev, passwordchangeduration: error }));
              }}
            />
            {errors.passwordchangeduration && <span className={styles.error}>{errors.passwordchangeduration}</span>}
          </div>
          <div className={styles.formGroup}>
            <label>Password Expire Notification:</label>
            <input
              type="text"
              value={passwordexpirenotification}
              onChange={(e) => {
                setPasswordExpireNotification(e.target.value);
                const error = validatePasswordExpireNotification(e.target.value);
                setErrors(prev => ({ ...prev, passwordexpirenotification: error }));
              }}
            />
            {errors.passwordexpirenotification && <span className={styles.error}>{errors.passwordexpirenotification}</span>}
          </div>
        </>
      )}

      <div className={styles.formGroup}>
        <label>Status:</label>
        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        >
          <option value="">Select</option>
          <option value="Active">Active</option>
          <option value="Inactive">Inactive</option>
        </select>
        {errors.status && <span className={styles.error}>{errors.status}</span>}
      </div>

      <div className={styles.formActions}>
        <button type="submit">Submit</button>
        <button type="button" onClick={handleReset}>Reset</button>
      </div>
    </form>
  );
}

export default AddPolicy;
